############################# TOKEN PRICE #######################################

def token_price(pair_token_wbnb_address, abi_token_pair, decimals_token, price_BNB, web3):
    pair_token_WBNB_address = Web3.toChecksumAddress(pair_token_wbnb_address)
    pair_token_WBNB_abi = abi_token_pair
    contract_pair = web3.eth.contract(address=pair_token_WBNB_address, abi=pair_token_WBNB_abi)

    nb_token, nb_WBNB, last_block = contract_pair.functions.getReserves().call()
    nb_WBNB = nb_WBNB * 10 ** -18
    nb_token = nb_token * 10 ** -decimals_token
    price_token = nb_WBNB * price_BNB / nb_token
    return price_token


############################# BURN AMOUNT #######################################
def total_burn_and_total_supply(burn_address, address_token, abi_token, web3):
    burn_address = Web3.toChecksumAddress(burn_address)
    contract_cummies = web3.eth.contract(address=address_token, abi=abi_token)
    return contract_cummies.functions.balanceOf(burn_address).call(), contract_cummies.functions.totalSupply().call()


############################# Market Cap #######################################
def market_cap(total_supply, total_burn, price_token, decimals_token):
    return (total_supply - total_burn) * price_token * 10 ** -18


