def create_message_price():
  while True:
    try:
      holders = nb_holders(bscscan_address)
      wbnb_price = bnb_price(web3)
      price_token = token_price(address_pair, abi_pair, decimals, wbnb_price, web3)
      total_burn, total_supply = total_burn_and_total_supply(address_burn, address_token, abi_token, web3)
      market_c = market_cap(total_supply, total_burn, price_token, decimals)
      burn_ratio = (total_burn / total_supply) * 100
      s = f"🚀🚀🚀 __{unity}__ Stats 🚀🚀🚀\n\n" \
        f"💰 *Current Price* : {price_token:6f} \n\n " \
        f"👫 *Holders* : {holders} \n\n " \
        f"🏦 *Market Cap* : {market_c:,.2f}\n\n"\
        f"🥀 *Total Burn* : {burn_ratio:.2f}%\n\n" \
        f"8\=\=\=\=\=\=\=\=\=D💦\n\n"\
        f"〽️ Chart : [DexTools](https://www.dextools.io/app/pancakeswap/pair-explorer/0x02bff262b10b7ae1d2e4455ad4cae2d0a6ecf086)".replace(".", "\.")
      print("message was created")
      break
    except Exception:
      time.sleep(1)
      print("Message could not be created")

  return s
